import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Editing Entries`}</h1>
    <p>{`In this guide, we'll cover how to edit entries with `}<inlineCode parentName="p">{`react-tinacms-contentful`}</inlineCode>{`.`}</p>
    <h2>{`Editing A Single Entry`}</h2>
    <p>{`Editing a single entry can be done via the `}<inlineCode parentName="p">{`useContentfulEntryForm`}</inlineCode>{` hook.`}</p>
    <p>{`Fetch an entry and pass it to `}<inlineCode parentName="p">{`useContentfulEntryForm`}</inlineCode>{`, and you will receive a `}<inlineCode parentName="p">{`modifiedEntry`}</inlineCode>{` and `}<inlineCode parentName="p">{`form`}</inlineCode>{` object.`}</p>
    <pre><code parentName="pre" {...{}}>{`export function Page() {
  const [entry, loading, error] = useContentfulEntry(entryId);
  const [modifiedEntry, form] = useContentfulEntryForm(entry);

  if (error) {
    return <div>Error: {error.message}</div>
  }

  return (
    <main>
      {loading && "Loading..."}
      {entry && entry.fields.title}
    </main>
  )
}
`}</code></pre>
    <hr></hr>
    <p>{`You can also pass custom `}<inlineCode parentName="p">{`FormOptions`}</inlineCode>{` to the hook to further customize the form:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff"
      }}>{`export function Page() {
  const [entry, loading, error] = useContentfulEntry(entryId);
-  const [modifiedEntry, form] = useContentfulEntryForm(entry);
+  const [modifiedEntry, form] = useContentfulEntryForm(entry, {
+    id: "page",
+    onChange: (values) => console.log(values)
+  });

  if (error) {
    return <div>Error: {error.message}</div>
  }

  return (
    <main>
      {loading && "Loading..."}
      {entry && entry.fields.title}
    </main>
  )
}
`}</code></pre>
    <ul>
      <li parentName="ul">{`explain useContentfulEntryForm and usePlugin`}</li>
      <li parentName="ul">{`explain useContentfulEntry with useForm and usePlugin`}</li>
    </ul>
    <h3>{`FAQs`}</h3>
    <ul>
      <li parentName="ul">{`What happens on write?`}</li>
      <li parentName="ul">{`What happens to references?`}</li>
    </ul>
    <h2>{`Editing Nested & Referenced Entries`}</h2>
    <ul>
      <li parentName="ul">{`explain useContentfulEntryForm with { references: true }`}</li>
      <li parentName="ul">{`explain cms.api.updateEntry`}</li>
      <li parentName="ul">{`expain cms.api.updateEntries`}</li>
    </ul>
    <h3>{`FAQs`}</h3>
    <ul>
      <li parentName="ul">{`What happens on write?`}</li>
      <li parentName="ul">{`What happens to references?`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
