import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h2><inlineCode parentName="h2">{`tinacms-contentful`}</inlineCode></h2>
    <p>{`A series of libraries aimed to make using TinaCMS with Contentful easy.`}</p>
    <h2>{`Pre-requisites`}</h2>
    <p>{`This documentation assumes you:`}</p>
    <ul>
      <li parentName="ul">{`Are comfortable with `}<a parentName="li" {...{
          "href": "https://tinacms.org/docs"
        }}>{`TinaCMS`}</a></li>
      <li parentName="ul">{`Have a working `}<a parentName="li" {...{
          "href": "https://tinacms.org/guides"
        }}>{`Tina site`}</a>{` ready to setup with Contentful`}</li>
      <li parentName="ul">{`Have a Contentful account and have setup access tokens to your space`}</li>
    </ul>
    <h2>{`Usage`}</h2>
    <p>{`First add the package to your site, along with the necessary contentful libraries:`}</p>
    <p><inlineCode parentName="p">{`npm install react-tinacms-contentful contentful contentful-management`}</inlineCode></p>
    <p>{`Then wrap the CMS with the provider:`}</p>
    <pre><code parentName="pre" {...{}}>{`const cms = new TinaCMS();

export const Cms({children}) {
  return (
    <TinaProvider cms={cms}>
      <ContentfulProvider>
        {children}
      </ContentfulProvider>
    </TinaProvider>
  )
}
`}</code></pre>
    <p>{`Then we'll need to configure the CMS to talk to contentful. We'll pull any "secret" values out of environment variables, so they can be changed dynamically at deploy time.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff"
      }}>{`+ import { ContentfulApiClient } from 'react-tinacms-contentful'
+
-const cms = new TinaCMS();
+const cms = new TinaCMS({
+  enabled: false,
+  sidebar: true,
+  apis: {
+    contentful: new ContentfulApiClient({
+      spaceId: process.env.CONTENTFUL_SPACE_ID,
+      defaultEnvironmentId: process.env.CONTENTFUL_DEFAULT_ENVIRONMENT_ID,
+      accessTokens: {
+       delivery: process.env.CONTENTFUL_DELIVERY_ACCESS_TOKEN,
+       preview: process.env.CONTENTFUL_DELIVERY_ACCESS_TOKEN
+      },
+      redirectUrl: "/"
+    })
+  }
+});

export const Cms({children}) {
  return (
    <TinaProvider cms={cms}>
      <ContentfulProvider>
        {children}
      </ContentfulProvider>
    </TinaProvider>
  )
}
`}</code></pre>
    <p>{`You'll need to create a `}<inlineCode parentName="p">{`.env`}</inlineCode>{` file to store those `}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
