import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Fetching Entries`}</h1>
    <hr></hr>
    <p>{`On this page, we'll cover how to fetch entries with `}<inlineCode parentName="p">{`react-tinacms-contentful`}</inlineCode>{`.`}</p>
    <h2>{`Fetching An Entry`}</h2>
    <p>{`You can fetch any entry in the space by ID.`}</p>
    <h3>{`Vanilla JS`}</h3>
    <p>{`To fetch an entry with Vanilla JS, use the `}<inlineCode parentName="p">{`cms.api`}</inlineCode>{` object.`}</p>
    <pre><code parentName="pre" {...{}}>{`const cms = new TinaCMS(...)

cms.api.contentful.getEntry("ENTRY_ID")
  .then(entry => console.log(entry))
`}</code></pre>
    <p><em parentName="p">{`To fetch only drafts, set `}<inlineCode parentName="em">{`preview`}</inlineCode>{` to true in the `}{`[options]`}{`(TODO: API DOCS).`}</em></p>
    <h3>{`React`}</h3>
    <p>{`To fetch an entry with React, use the `}<inlineCode parentName="p">{`useContentfulEntry`}</inlineCode>{` hook:`}</p>
    <pre><code parentName="pre" {...{}}>{`import { useContentfulEntry } from 'react-tinacms-contentful'

export const Example () => {
  const [entry, loading, error] = useContentfulEntry(ENTRY_ID)

  if (error) {
    return (
      <>
        <h2>Something went wrong...</h2>
        <pre>
          <code>{JSON.stringify(error.message)}</code>
        </pre>
      </>
    )
  }

  return (
    <>
      {loading && "Loading..."}
      {entry && JSON.stringify(entry)}
  )
}
`}</code></pre>
    <p><em parentName="p">{`To fetch only drafts, set `}<inlineCode parentName="em">{`preview`}</inlineCode>{` to true in the `}{`[options]`}{`(TODO: API DOCS).`}</em></p>
    <h2>{`Fetching Many Entries`}</h2>
    <p>{`You can fetch all entries in the space, or search for entries by providing a query object with `}<a parentName="p" {...{
        "href": "https://www.contentful.com/developers/docs/references/content-delivery-api/#/reference/search-parameters/order"
      }}>{`search parameters`}</a>{`:`}</p>
    <h3>{`Vanilla JS`}</h3>
    <pre><code parentName="pre" {...{}}>{`const cms = new TinaCMS(...)

// Get all entries
cms.api.contentful.getEntries()
  .then(allEntries => console.log(allEntries))

// Search for entries
cms.api.contentful.getEntries({
  content_type: 'post',
  order: 'sys.createdAt'
})
  .then(posts => console.log(posts))
`}</code></pre>
    <p><em parentName="p">{`To fetch only drafts, set `}<inlineCode parentName="em">{`preview`}</inlineCode>{` to true in the `}{`[options]`}{`(TODO: API DOCS).`}</em></p>
    <h3>{`React`}</h3>
    <p>{`To fetch an entry with React, use the `}<inlineCode parentName="p">{`useContentfulEntry`}</inlineCode>{` hook:`}</p>
    <pre><code parentName="pre" {...{}}>{`import { useContentfulEntry } from 'react-tinacms-contentful'

export const Example () => {
  const [allEntries, allLoading, errorA] = useContenfulEntries()
  const [posts, postsLoading, errorB] = useContentfulEntries({
    content_type: 'post',
    order: 'sys.createdAt'
  })

  if (errorA || error B) {
    return (
      <>
        <h2>Something went wrong...</h2>
        <pre>
          <code>{JSON.stringify(errorA.message || errorB.message)}</code>
        </pre>
      </>
    )
  }

  return (
    <>
      <ol>
        {allLoading && "Loading..."}
        {allEntries && allEntries.map(entry => <li>{JSON.stringify(entry)})}</li>)}
      </ol>
      <ol>
        {postsLoading && "Loading..."}
        {posts && posts.map(entry => <li>{JSON.stringify(entry)})}</li>)}
      </ol>
  )
}
`}</code></pre>
    <p><em parentName="p">{`To fetch only drafts, set `}<inlineCode parentName="em">{`preview`}</inlineCode>{` to true in the `}{`[options]`}{`(TODO: API DOCS).`}</em></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
