import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Using Contentful in TinaCMS Plugins`}</h1>
    <hr></hr>
    <p>{`To use Contentful in `}<a parentName="p" {...{
        "href": "https://tina.io/docs/plugins/"
      }}>{`TinaCMS plugins`}</a>{`, you can either use the `}<a parentName="p" {...{
        "href": "#cms-api"
      }}><inlineCode parentName="a">{`cms.api`}</inlineCode></a>{` object, or the `}<a parentName="p" {...{
        "href": "#react-utility-hooks"
      }}>{`react utility hooks`}</a>{`.`}</p>
    <h2>{`CMS API`}</h2>
    <p>{`Inside various plugins types you will be given access to the `}<inlineCode parentName="p">{`cms`}</inlineCode>{` object. You can access the `}<inlineCode parentName="p">{`ContentfulAPIClient`}</inlineCode>{` from this. For example, in a `}{`[content creator plugin]`}{`)():`}</p>
    <pre><code parentName="pre" {...{}}>{`const PostCreatorPlugin = {
  __type: 'content-creator',
  fields: [
    { name: "fields.title", label: "Title", component: "text" }
  ],
  onSubmit: aysnc(values, cms) {
    try {
      await cms.api.contentful.createEntry(values.fields)

      cms.alerts.success("Created entry")
    }
    catch (error) {
      cms.alerts.error(error.message)
    }
`}</code></pre>
    <h2>{`React Utility Hooks`}</h2>
    <p>{`There are a variety of React utility hooks for interacting with Contentful:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`useContentful`}</inlineCode>{`: returns the `}<inlineCode parentName="li">{`ContentfulAPIClient`}</inlineCode>{` instance`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`useContentfulDelivery`}</inlineCode>{`: returns an instance of the Contentful JS SDK to retreive drafts from the delivery API.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`useContentfulPreview`}</inlineCode>{`: returns an instance of the Contentful JS SDK to retreive drafts from the preview API.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`useContentfulPreview`}</inlineCode>{`: returns an instance of the Contentful JS SDK to retreive drafts from the preview API.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`useContentfulManagement`}</inlineCode>{`: returns an instance of the Contentful Management API for the current logged in Contentful user.`}
        <blockquote parentName="li">
          <p parentName="blockquote"><strong parentName="p">{`Note:`}</strong>{` do not use this in contexts where the CMS is not enabled and a user is not logged in.`}</p>
        </blockquote>
      </li>
      <li parentName="ul"></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
