import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const TinaProvider = makeShortcode("TinaProvider");
const ContentfulProvider = makeShortcode("ContentfulProvider");
const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h2><inlineCode parentName="h2">{`tinacms-contentful`}</inlineCode></h2>
    <p>{`A series of libraries aimed to make using TinaCMS with Contentful easy.`}</p>
    <h2>{`Pre-requisites`}</h2>
    <p>{`This documentation assumes you:`}</p>
    <ul>
      <li parentName="ul">{`Are comfortable with `}<a parentName="li" {...{
          "href": "https://tinacms.org/docs"
        }}>{`TinaCMS`}</a></li>
      <li parentName="ul">{`Have a working `}<a parentName="li" {...{
          "href": "https://tinacms.org/guides"
        }}>{`Tina site`}</a>{` ready to setup with Contentful`}</li>
      <li parentName="ul">{`Have a Contentful account and have setup access tokens to your space`}</li>
    </ul>
    <h2>{`Usage`}</h2>
    <p>{`First add the package to your site, along with the necessary contentful libraries:`}</p>
    <blockquote>
      <blockquote parentName="blockquote">
        <p parentName="blockquote">{`npm install react-tinacms-contentful contentful contentful-management`}</p>
      </blockquote>
    </blockquote>
    <p>{`Then wrap the CMS with the provider:`}</p>
    <blockquote>
      <p parentName="blockquote">{`const cms = new TinaCMS();`}</p>
      <p parentName="blockquote">{`export const Cms({children}) {
return (`}</p>
   <TinaProvider cms={cms} mdxType="TinaProvider">
     <ContentfulProvider mdxType="ContentfulProvider">
       {children}
     </ContentfulProvider>
   </TinaProvider>
 )
}
    </blockquote>
    <p>{`Then configure the CMS to talk to contentful`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
